nome = "Vinícius Raniery Pereira"

print(nome[0]) # Isso seria um método de uma string em Python que retorna o caractere na posição 0 da string.
print(nome[1]) # Isso seria um método de uma string em Python que retorna o caractere na posição 1 da string.
print(nome[:9]) # Isso seria um método de uma string em Python que retorna os caracteres da posição 0 até a posição 9 da string.
print(nome[10:]) # Isso seria um método de uma string em Python que retorna os caracteres da posição 10 até o final da string.
print(nome[10:16]) # Isso seria um método de uma string em Python que retorna os caracteres da posição 10 até a posição 16 da string.
print(nome[10:16:2]) # Isso seria um método de uma string em Python que retorna os caracteres da posição 10 até a posição 16 da string, pulando de 2 em 2.
print(nome[:]) # Isso seria um método de uma string em Python que retorna todos os caracteres da string.
print(nome[::-1]) # Isso seria um método de uma string em Python que retorna todos os caracteres da string, mas na ordem inversa.